(()=>{document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll("pre").forEach(t=>{let e=document.createElement("button");e.innerText="Copy",e.classList.add("copy-btn"),t.appendChild(e),e.addEventListener("click",()=>{let n=t.querySelector("code");n&&navigator.clipboard.writeText(n.innerText).then(()=>{e.innerText="Copied!",setTimeout(()=>e.innerText="Copy",2e3)})})})});})();
